def pos_of_packet_start(str_in, window_size):
    for pos in range(window_size, len(str_in)):
        window = set(str_in[pos-window_size:pos])
        if len(window) == window_size:
            return pos
    return len(window_size)

if __name__ == "__main__":
    print(pos_of_packet_start('bvwbjplbgvbhsrlpgdmjqwftvncz', 4))
    print(pos_of_packet_start('nppdvjthqldpwncqszvftbrmjlhg', 4))
    print(pos_of_packet_start('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg', 4))
    print(pos_of_packet_start('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw', 4))
